script:
qr_created = False

source_warehouse = ''
variety = ''
number_of_stems = 0
grower = ''
breeder = ''

for row in doc.items:
    print(f'Processing item: {row}')

    if row.t_warehouse != "Field Rejects - TL":
        qr_created = True
        break

for row in doc.items:
    if row.s_warehouse:
        source_warehouse = row.s_warehouse
    if row.item_code:
        variety = row.item_code
    if row.custom_number_of_stems:
        number_of_stems = row.custom_number_of_stems
    if row.custom_grower:
        grower = row.custom_grower
    if row.custom_breeder:
        breeder = row.custom_breeder


if doc.stock_entry_type  == "Planting":
    qr_created = False

stock_entry_details = {
    "name": doc.name,
    "source_warehouse": source_warehouse,
    "variety": variety,
    "number_of_stems": number_of_stems,
    "grower": grower,
    "breeder": breeder
}


if not doc.custom_qr_code and qr_created:
    qr_code_url = frappe.call(
        "upande_tambuzi.server_scripts.qr_code_generator.generate_qr_code_with_bucket_details",
        stock_entry_details=stock_entry_details,

    )
    doc.custom_qr_code = qr_code_url

name = doc.name

if doc.custom_qr_code:
    qr_code_doc = frappe.get_doc({
        "doctype": "QR Code",  
        "stock_entry": doc.name,
        "qr_code_image": doc.custom_qr_code,  
    })
    qr_code_doc.insert()
