script:
all_target_WH_rejects = True

for row in doc.items:
    if row.t_warehouse != "Field Rejects - TL":
        all_target_WH_rejects = False
        break


if not doc.custom_qr_code and not all_target_WH_rejects:
    qr_code_url = frappe.call(
        "upande_tambuzi.server_scripts.qr_code_generator.generate_qr_code",
        stock_entry_name=doc.name, 
    )
    doc.custom_qr_code = qr_code_url
