if doc.stock_entry_type == "Grading":
    name = doc.name

    if frappe.db.exists("QR Code", name):
        frappe.delete_doc("QR Code", name)

    if doc.custom_bunch_qr_code:
        qr_code_doc = frappe.get_doc({
            "doctype": "QR Code",  
            "stock_entry": doc.name,
            "qr_code_image": doc.custom_bunch_qr_code,  
        })
        qr_code_doc.insert()
